// 判断浏览器类型及版本
function getBrowserInfo(){
    var Sys = {};
    var userAgent = navigator.userAgent.toLowerCase();
    var reg =/(msie|firefox|chrome|opera|version).*?([\d.]+)/;
    var m = userAgent.match(reg);

    Sys.browser = m[1].replace(/version/, "'safari");
    Sys.version = m[2];
    return Sys;
}

